Rule.code:
/*
Profile to update count of fund transfer transactions for loan repayment or card debt settlement.
Also stores count of fund transfers to watchlisted accounts.
*/

IF( SMH_CLIENT_TRAN_TYPE = 'DEBIT' ) THEN DO;
	IF( _U_8BYTE_STRING_003 IN ( 'MB', 'IB' ) ) THEN DO;
		/*Loan or card payment purpose*/
		IF( TBT_BILL_CAT IN ( 'CA', 'ML' ) ) THEN DO;
			IF( _A_LOAN_CC_PAY_PROFILE_DATE EQ RQO_TRAN_DATE ) THEN DO;
				%SET( _A_NO_TRANS_LOAN_CC_PAY ) = _A_NO_TRANS_LOAN_CC_PAY + 1;
			END;
			ELSE DO;
				%SET( _A_LOAN_CC_PAY_PROFILE_DATE ) = RQO_TRAN_DATE;
				%SET( _A_NO_TRANS_LOAN_CC_PAY ) = 1;
			END;
		END;

		/*Fund transfer to watchlisted accounts*/
		/*TO DO: CREATE WATCHLIST AND UPDATE NAME*/
		IF( %ListContains(at_risk_card,TPP_ACCT_NUM)) THEN DO;
			IF( _A_FUND_TRF_WATCHLIST_DATE EQ RQO_TRAN_DATE ) THEN DO;
				%SET( _A_NO_FUND_TRF_WATCHLIST ) = _A_NO_FUND_TRF_WATCHLIST + 1;
			END;
			ELSE DO;
				%SET( _A_FUND_TRF_WATCHLIST_DATE ) = RQO_TRAN_DATE;
				%SET( _A_NO_FUND_TRF_WATCHLIST ) = 1;
			END;
		END;
	END;
END;

/*
Profile to store CNP transactions counts for the past 72 hours
*/

IF( SMH_CLIENT_TRAN_TYPE EQ 'DEBIT' ) THEN DO;
	IF( _U_8BYTE_STRING_003 IN ( 'ECOM' ) ) THEN DO;
		/*Card not present*/
		IF( UCM_CARD_PRESENT EQ '1' ) THEN DO;
			%DeclareArray(&RULE.CNP_TIME, _C_CNP_TIMESTAMP_HOUR_1 - _C_CNP_TIMESTAMP_HOUR_72);
			%DeclareArray(&RULE.CNP_COUNT, _C_CNP_HOUR_COUNT_1 - _C_CNP_HOUR_COUNT_72);

			/*If date and hour are same*/
			IF( _C_CNP_TIMESTAMP_HOUR_1 NE . AND
			RQO_TRAN_DATE EQ DATEPART( _C_CNP_TIMESTAMP_HOUR_1 ) AND
			HOUR( RQO_TRAN_TIME ) EQ HOUR( _C_CNP_TIMESTAMP_HOUR_1 )
			) THEN DO;
				%SET( _C_CNP_HOUR_COUNT_1 ) = _C_CNP_HOUR_COUNT_1 + 1;
			END;

			ELSE DO;
				%ShiftHistoryArray(&RULE.CNP_TIME);
				%ShiftHistoryArray(&RULE.CNP_COUNT);

				%SET( _C_CNP_HOUR_COUNT_1 ) =  1;
				%SET( _C_CNP_TIMESTAMP_HOUR_1 ) = DHMS( RQO_TRAN_DATE, 0, 0, RQO_TRAN_TIME );
			END;


			/*Count aggregate for 72 hours*/
			%SET( _C_CNP_LAST_72_HOURS ) = 0;
			DO &RULE.I = 1 TO 72;
				IF( DHMS(RQO_TRAN_DATE, 0, 0, RQO_TRAN_TIME) - %GET( &RULE.CNP_TIME, &RULE.I )
					< DHMS( 0, 72, 0, 0))
				THEN DO;
					%SET( _C_CNP_LAST_72_HOURS ) = _C_CNP_LAST_72_HOURS + %GET( &RULE.CNP_COUNT, &RULE.I );
				END;
			END;
		END;
	END;
END;

/*
Profile to store timestamp of transaction if it is made to a blacklisted account
*/

IF( SMH_CLIENT_TRAN_TYPE EQ 'DEBIT' ) THEN DO;
	IF( _U_8BYTE_STRING_003 IN ( 'ECOM' ) ) THEN DO;
		/*TODO: VARIABLE NAME CHANGE*/
		/*Exposed info blacklist*/
		IF( %ListContains(at_risk_card, _U_20BYTE_STRING_001) ) THEN DO;
			%SET( _C_INFO_BLACKLIST_TXN_DT ) = DHMS( RQO_TRAN_DATE, 0, 0, RQO_TRAN_TIME );
		END;

		/*Blacklisted merchant*/
		IF( %ListContains(at_risk_card, _U_20BYTE_STRING_001) ) THEN DO;

			IF(DHMS(RQO_TRAN_DATE, 0, 0, RQO_TRAN_TIME) - _C_INFO_BLACKLIST_TXN_DT
			< DHMS( 90, 0, 0, 0 ))

			THEN %SET( _C_TXN_TO_BLACKLIST_WITHIN_90D ) = 1;

			ELSE %SET( _C_TXN_TO_BLACKLIST_WITHIN_90D ) = 0;
		END;
	END;
END;

/*
Profile to store count of credit transactions for a merchant for the past 7 months
*/

IF( SMH_CLIENT_TRAN_TYPE EQ 'CREDIT' ) THEN DO;
	IF( DUA_8BYTE_STRING_002 IN ('IB', 'MB') ) THEN DO;
		/*Merchant ID is not empty*/
		IF( _U_20BYTE_STRING_001 NE '' ) THEN DO;
			%DeclareArray(&RULE.TXN_MONTH, _X_TXN_MONTH_1 - _X_TXN_MONTH_7);
			%DeclareArray(&RULE.TXN_COUNT, _X_AMT_TXN_MONTH_1 - _X_AMT_TXN_MONTH_7);

			IF MONTH( _X_TXN_MONTH_1 ) EQ MONTH( RQO_TRAN_DATE ) AND
				YEAR( _X_TXN_MONTH_1 ) EQ YEAR( RQO_TRAN_DATE ) THEN DO;

			%SET( _X_AMT_TXN_MONTH_1 ) = _X_AMT_TXN_MONTH_1 + TBT_TRAN_AMT;
			END;

			ELSE DO;
				%ShiftHistoryArray(&RULE.TXN_MONTH);
				%ShiftHistoryArray(&RULE.TXN_COUNT);

				%SET( _X_TXN_MONTH_1 ) = RQO_TRAN_DATE;
				%SET( _X_AMT_TXN_MONTH_1 ) = TBT_TRAN_AMT;
			END;

			/*Calculate aggregate amount for 6 months*/
			&RULE.AGG_AMT_6MONTH = 0;
			DO &RULE.I = 1 TO 7;
				IF (RQO_TRAN_DATE - %GET(&RULE.TXN_MONTH, &RULE.I) < DHMS(6, 0, 0, 0)) THEN DO;
					&RULE.AGG_AMT_6MONTH = &RULE.AGG_AMT_6MONTH + %GET( &RULE.TXN_COUNT, &RULE.I );
				END;
			END;

			/*Subtract current month amount*/
			IF( MONTH( RQO_TRAN_DATE ) = MONTH( _X_TXN_MONTH_1 ) AND YEAR( RQO_TRAN_DATE ) = YEAR( _X_TXN_MONTH_1 ) )
			THEN DO;
				&RULE.AGG_AMT_6MONTH = &RULE.AGG_AMT_6MONTH - _X_AMT_TXN_MONTH_1;
			END;

			/*Calculate spike*/
			%SET( _X_SPIKE_TXN_AMT_CURRENT_MON ) = _X_AMT_TXN_MONTH_1/&RULE.AGG_AMT_6MONTH;
		END;
	END;
END;

if ((SMH_CLIENT_TRAN_TYPE EQ 'DEBIT')
& (UCM_CARD_PRESENT EQ '1')
& (_C_CNP_LAST_72_HOURS >=  11))
then do;
%Action_Approve();
%Action_Alert;
end;

if ((SMH_CLIENT_TRAN_TYPE EQ 'DEBIT')
& ( _U_8BYTE_STRING_003 IN ('MB',
'IB')
 )
& (_A_NO_TRANS_LOAN_CC_PAY >=  5))
then do;
%Action_Alert;
end;

if ((SMH_CLIENT_TRAN_TYPE EQ ''DEBIT'')
& ( _U_8BYTE_STRING_003 IN ('MB',
'IB')
 )
& (_A_NO_FUND_TRF_WATCHLIST >=  5))
then do;
end;

if ((SMH_CLIENT_TRAN_TYPE EQ 'DEBIT')
& ( _U_8BYTE_STRING_003 IN ('ECOM')
 )
& (_C_TXN_TO_BLACKLIST_WITHIN_90D EQ  1))
then do;
%Action_Alert;
end;

if ((SMH_CLIENT_TRAN_TYPE EQ 'CREDIT')
& ( _U_8BYTE_STRING_003 IN ('IB',
'MB')
 )
& (_X_SPIKE_TXN_AMT_CURRENT_MON >=  5))
then do;
%Action_Alert;
end;
